    public fmMostrar() {
        initComponents();
        
        try{
            DefaultTableModel m1= new DefaultTableModel();
            Datos2.setModel(m1);
            
            Conexion conn=new Conexion();
            Connection con= conn.getConexion();
            PreparedStatement ps=null;
            ResultSet rs=null;
            
            String sql="SELECT cedula,nombre,apellido1,apellido2,nacionalidad,fechaNaci,enfermedad,correo,telefono,direccion,seccion,especialidad,cedulab,beneficiario,apellido1b,apellido2b,funcionari,funcion,fechaIngreso FROM tb_afiliacion";
            ps=con.prepareStatement(sql);
            rs=ps.executeQuery();
            
            ResultSetMetaData rsMd=rs.getMetaData();
            int cantidadColumn=rsMd.getColumnCount();
            
            m1.addColumn("Cedula");
       
            m1.addColumn("Nombre");
            m1.addColumn("Apellido1");
            m1.addColumn("Apellido2");
            m1.addColumn("Nacionalidad");
            m1.addColumn("Fecha de Nacimiento");
            m1.addColumn("Enfermedad");
            m1.addColumn("Correo Electronico");
            m1.addColumn("Numero de Telefono");
            m1.addColumn("Direccion");
            m1.addColumn("Seccion");
            m1.addColumn("Especialidad/Taller");
            m1.addColumn("Cedula");
            m1.addColumn("Beneficiario");
            m1.addColumn("Apellido1");
            m1.addColumn("Apellido2");
            m1.addColumn("Funcionario");
            m1.addColumn("Funcion/Cargo");
            m1.addColumn("Fecha de Ingreso");
            
            while(rs.next()){
                
                Object[] filas= new Object[cantidadColumn];
                
                for(int i=0; i<cantidadColumn; i++){
                    filas[i]=rs.getObject(i+1);
                }
                
                m1.addRow(filas);
            }
            
        }catch(SQLException ex){
            System.err.println(ex.toString());
        }
    }

